import{initializeApp}from"firebase/app";import{getAuth,connectAuthEmulator}from"firebase/auth";import{getFirestore,connectFirestoreEmulator}from"firebase/firestore";const firebaseConfig={apiKey:import.meta.env.VITE_FIREBASE_API_KEY,authDomain:import.meta.env.VITE_FIREBASE_AUTH_DOMAIN,projectId:import.meta.env.VITE_FIREBASE_PROJECT_ID,storageBucket:import.meta.env.VITE_FIREBASE_STORAGE_BUCKET,messagingSenderId:import.meta.env.VITE_FIREBASE_MESSAGING_SENDER_ID,appId:import.meta.env.VITE_FIREBASE_APP_ID},app=initializeApp(firebaseConfig);export const auth=getAuth(app);export const db=getFirestore(app);if("localhost"===window.location.hostname){const checkEmulator=async port=>{try{return 404!==(await fetch(`http://localhost:${port}`)).status}catch{return!1}};Promise.all([checkEmulator(9099),checkEmulator(8088)]).then((([authRunning,firestoreRunning])=>{authRunning?(connectAuthEmulator(auth,"http://localhost:9099"),console.log("✅ Connected to Auth emulator")):console.warn("⚠️ Auth emulator not running. Start with: firebase emulators:start"),firestoreRunning?(connectFirestoreEmulator(db,"localhost",8088),console.log("✅ Connected to Firestore emulator")):console.warn("⚠️ Firestore emulator not running. Start with: firebase emulators:start")}))}