export function formatNumber(num){return null==num?"0":num.toLocaleString("es-MX",{minimumFractionDigits:2,maximumFractionDigits:2})}export function handleError(error,message="Error al cargar los datos"){console.error("Error:",error);const loadingMessage=document.getElementById("loadingMessage");loadingMessage&&(loadingMessage.textContent=message)}export function formatDate(date){return`${date.getDate().toString().padStart(2,"0")}-${["Ene","Feb","Mar","Abr","May","Jun","Jul","Ago","Sep","Oct","Nov","Dic"][date.getMonth()]}-${date.getFullYear()}`}export function toTitleCase(str){const specialCases={"sa de cv":"SA de CV","sapi de cv":"SAPI de CV","srl de cv":"SRL de CV","s de rl":"S de RL","s de rl de cv":"S de RL de CV","sc de rl":"SC de RL"},lowercaseWords=["de","del","la","las","el","los","y","e","o","u"],lowerStr=str.toLowerCase();for(const[suffix,replacement]of Object.entries(specialCases))if(lowerStr.endsWith(` ${suffix}`)){return`${str.slice(0,-suffix.length).trim().toLowerCase().split(" ").map(((word,index)=>0===index?word.charAt(0).toUpperCase()+word.slice(1):lowercaseWords.includes(word)?word:word.charAt(0).toUpperCase()+word.slice(1))).join(" ")} ${replacement}`}return str.toLowerCase().split(" ").map(((word,index)=>0===index?word.charAt(0).toUpperCase()+word.slice(1):lowercaseWords.includes(word)?word:word.charAt(0).toUpperCase()+word.slice(1))).join(" ")}